package com.cobaltplatform.api.cache;

import javax.inject.Qualifier;
import java.lang.annotation.Retention;
import java.lang.annotation.Target;

import static java.lang.annotation.ElementType.FIELD;
import static java.lang.annotation.ElementType.METHOD;
import static java.lang.annotation.ElementType.PARAMETER;
import static java.lang.annotation.RetentionPolicy.RUNTIME;

/**
 * Injection annotation which indicates data should be pulled from a request-scoped temporary data.
 * <p>
 * This is useful for scenarios where we want a temporary non-distributed cache which avoids race conditions
 * (e.g. reusing expensive not-threadsafe NumberFormat instances on the same thread)
 *
 * @author Transmogrify, LLC.
 */
@Qualifier
@Target({FIELD, PARAMETER, METHOD})
@Retention(RUNTIME)
public @interface CurrentContextCache {
	// No attributes
}